import React from 'react';
import classes from './ServiceSideMenu.module.css';


const serviceSideMenu = (props) => {
    const links = props.services.map((service, index) => {
        const modifiedName = service.replaceAll("_"," ");
        let activeClass = '';
        if(props.service === props.activeService){
            activeClass = classes.active;
        }
        return (<div key={index}  onClick={() => props.clicked(service)} className={classes.List}>{modifiedName}</div>)
    })
    return (
        <div className={classes.ServiceSideMenu}>
            <h2>{props.title}</h2>
            <hr></hr>
            {links}
        </div>
    );
}

export default serviceSideMenu;
