import React, { Component }from 'react';
import classes from './GST.css';
import ServiceSideMenu from '../../UI/ServiceSideMenu/ServiceSideMenu';
import GstRegistration from '../GST/GST_Registration/GST_Registration';
import GstFiling from '../GST/GST_Filing/GST_Filing';

class Gst extends Component {

    state = {
        selectedService : 'GST_Registration', 
        selectedMenu: 'Definition',
    }
    services = [
        "GST_Registration",
        "GST_Filing",
    ];
    GstRegistrationMenu = [
        "Definition",
        "Inclusion",
        "Procedures",
        "Documents",
        "Advantages",
        "FAQs"
    ]
    changeServiceHandler = (service) => {
        this.setState({
            selectedService:service
        })
    }
    changeMenuHandler = (menu) => {
        this.setState({
            selectedMenu: menu
        })
    }
    render() {
        let activeService = null ;
        switch(this.state.selectedService){
            case this.services[0]:
                activeService = (<GstRegistration menu={this.GstRegistrationMenu} clicked={this.changeMenuHandler} selectedMenu={this.state.selectedMenu}/>);
                break;
            case this.services[1]:
                activeService = (<GstFiling menu={this.GstRegistrationMenu} clicked={this.changeMenuHandler} selectedMenu={this.state.selectedMenu}/>);
                break;
            default:
                activeService=(<GstRegistration menu={this.GstRegistrationMenu} clicked={this.changeMenuHandler} selectedMenu={this.state.selectedMenu}/>);
        }
        return (
            <section className={classes.Gst}>
                <ServiceSideMenu services={this.services} title="GST" clicked={this.changeServiceHandler} activeService={this.state.selectedService}/>
                <div className={classes.ServiceDescription}>
                    {activeService}
                </div>
            </section>
        );
    }

}

export default Gst;
