import React, { Component }from 'react';
import classes from './Layout.module.css';
import Main from '../../containers/Main/Main';
import Sidebar from '../../UI/Sidebar/Sidebar';
import { Route, Switch, NavLink, Link} from 'react-router-dom';
import ContactForm from '../../containers/ContactForm/ContactForm';
import About from '../About/About';
import Services from '../../containers/Services/Services';
import Ionicon from 'react-ionicons';

class Layout extends Component{

    state = {
        show:false
    }

    sidebarHandler=() => {
        const shown = !this.state.show;
        this.setState({show:shown});
    }

    render(){
        let navStyles = null;
        if(this.state.show){
            navStyles={
                position:"fixed",
            }
        }
        let switched = null
        if(!this.state.Sidebar){
            switched=(
                <Switch>
                <Route path="/contact" component={ContactForm} />
                <Route path="/about" component={About} />
                <Route path="/services" component={Services}/>
                <Route path="/" component={Main}/>
                </Switch>
            )
        }
    return (

        <div className={classes.Layout}>
            <header >
                <nav className={classes.Nav} style={navStyles}>
                <p>logo</p>
                <div className={classes.Sidebar} onClick={this.sidebarHandler}>
                    <div></div>
                    <div></div>
                    <div></div>
                </div>
                <ul className={classes.MenuList}>
                    <li><NavLink to="/">Home</NavLink></li>
                    <li></li>
                    <li><NavLink to="/about">About</NavLink></li>
                    <li></li>
                    <li><NavLink to="/services" style={{position:"relative"}}>
                        <div className={classes.ListStyle}>
                            <div>Services</div>
                            <div style={{marginTop:"10px"}}>
                                <Ionicon icon="ios-arrow-down" fontSize="18px"  color="#f5f5f0"/>
                            </div>
                        </div>
                        <div className={classes.ServiceList}>
                            <div>
                            <div>Start Business</div>
                                <hr></hr>
                                <div>MCA</div>
                                <hr></hr>
                                <div>Income Tax</div>
                                <hr></hr>
                                <div>GST</div>
                                <hr></hr>
                                <div>RBI</div>
                                <hr></hr>
                                <div>Listing Compliances</div>
                                <hr></hr>
                                <div>Finance</div>
                                <hr></hr>
                                <div>Valuation</div>
                                <hr></hr>
                                <div>Other Services</div>

                            </div>

                        </div>
                        </NavLink>
                    </li>
                    <li></li>
                    <li><NavLink to="/knowledge">Knowledge Portal</NavLink></li>
                </ul>
                </nav>
            </header>
            <Sidebar visible={this.state.show}>
                <div className={classes.SidebarMenu}>
                <ul>
                    <li onClick={this.sidebarHandler}><NavLink to="/">Home</NavLink></li>
                    <li onClick={this.sidebarHandler}><NavLink to="/about">About</NavLink></li>
                    <li onClick={this.sidebarHandler}><NavLink to="/services">Services</NavLink></li>
                    <li onClick={this.sidebarHandler}><NavLink to="/knowledge">Knowledge Portal</NavLink></li>
                </ul>
                </div> 
            </Sidebar>

            {switched}

            {/* <Main /> */}
            <div className={classes.Contact}><Link to="/contact">Contact Us</Link></div>
            <footer>

            </footer>
        </div>

    );
    }
}

export default Layout;
